using Microsoft.Azure.Search;
using Microsoft.Azure.Search.Models;
using System;
using System.Collections.Generic;
using System.Linq;
using Microsoft.Spark.Extensions.Azure.Synapse.Analytics.Utils;

public static string searchServiceName = TokenLibrary.getSecret("LMT-KV-UAT-EASTUS", "LaborRejectionRecommendation-SearchServiceName", "LMT_KeyVault_Uat");
public static string searchIndex = TokenLibrary.getSecret("LMT-KV-UAT-EASTUS", "LaborRejectionRecommendation-SearchIndex", "LMT_KeyVault_Uat");
public static string KeyCol = TokenLibrary.getSecret("LMT-KV-UAT-EASTUS", "LaborRejectionRecommendation-SearchKeyColumn", "LMT_KeyVault_Uat");
public static string apiKey =TokenLibrary.getSecret("LMT-KV-UAT-EASTUS", "LaborRejectionRecommendation-SearchApiKey", "LMT_KeyVault_Uat");

while (true)
{
    SearchIndexClient indexClient = new SearchIndexClient(searchServiceName, searchIndex, new SearchCredentials(apiKey));
    DocumentSearchResult<Document> searchResult = indexClient.Documents.Search<Document>(string.Empty);
    List<string> azureDocsToDelete =
    searchResult
        .Results
        .Select(r => r.Document[KeyCol].ToString())
        .ToList();
    if (azureDocsToDelete.Count != 0)
    {
        try
        {
            var result = indexClient.Documents.Index(IndexBatch.Delete(KeyCol, azureDocsToDelete));
        }
        catch (IndexBatchException ex)
        {
            throw new Exception($"Failed to reset the index: {string.Join(", ", ex.IndexingResults.Where(r => !r.Succeeded).Select(r => r.Key))}");
        }
    }
    else
    {
        break;
    }
    
}

var serviceClient = new SearchServiceClient(searchServiceName, new SearchCredentials(apiKey));
serviceClient.Indexers.Run("idxr-rejectionrecommendation");
